mapper.select:
select * 
from schedule 
WHERE member_id = #{memberId} and start_date = '${date.toString()}';

mapper.insert:
insert into
schedule(start_date, start_time, end_date, end_time, title, member_id, background_color, latitude, longitude)		
values(#{startDate},#{startTime},#{endDate},#{endTime}, #{title},#{memberId},#{backgroundColor},#{latitude}, #{longitude});

mapper.update:
update schedule
start_date = #{startDate}, start_time = #{startTime}, end_date = #{endDate}, end_time = #{endTime}, title = #{title}, background_color = #{backgroundColor}, latitude = #{latitude}, longitude = #{longitude}
where id = #{id};
