mapper.select:
select *
FROM schedule
WHERE member_id = #{memberId} 
	AND start_date <= str_to_date(#{date}, '%Y-%m-%d')
	AND end_date  >= str_to_date(#{date}, '%Y-%m-%d');

mapper.insert:
insert into
schedule(start_date, start_time, end_date, end_time, title, member_id, background_color, latitude, longitude, place)		
values(#{startDate},#{startTime},#{endDate},#{endTime}, #{title},#{memberId},#{backgroundColor},#{latitude}, #{longitude}, #{place});

mapper.update:
update schedule
start_date = #{startDate}, start_time = #{startTime}, end_date = #{endDate}, end_time = #{endTime}, title = #{title}, background_color = #{backgroundColor}, latitude = #{latitude}, longitude = #{longitude} place = #{place}
where id = #{id};
